let r = requests | where $__timeFilter();
let f = toscalar(r | summarize min(timestamp));
let t = toscalar(r | summarize max(timestamp));
let span=(t - f)/60;
let interval=case(span >= 1d, bin(span, 1d), span >= 1h, bin(span, 1h), 15m);
let intervalHours = interval / 1h;
r
| where success == false and toint(resultCode) >= 500
| make-series valueCount=count() default=0 on timestamp in range(f, t, interval) by resultCode
| mv-expand timestamp, valueCount
| project timestamp=todatetime(timestamp), failuresCount=todouble(valueCount)/intervalHours, resultCode
